const zoom=2,thumb=document.getElementById("image-thumb"),previewElement=document.getElementById("preview-element"),zoomImageContainer=document.getElementById("zoom-image-container"),zoomImage=document.getElementById("zoom-image");zoomImageContainer.style.height=2*thumb.clientHeight+"px",zoomImageContainer.style.width=2*thumb.clientWidth+"px",previewElement.style.height=thumb.clientHeight/2+"px",previewElement.style.width=thumb.clientWidth/2+"px";const calculatePosition=(x,y)=>{const imageHeight=thumb.clientHeight,imageWidth=thumb.clientWidth,previewElementHeight=previewElement.clientHeight,previewElementWidth=previewElement.clientWidth;let positionX=x-previewElementWidth/2,positionY=y-previewElementHeight/2;positionX<0&&(positionX=0),positionY<0&&(positionY=0),y+previewElementHeight/2>imageHeight&&(positionY=imageHeight-previewElementHeight),x+previewElementWidth/2>imageWidth&&(positionX=imageWidth-previewElementWidth),previewElement.style.left=`${positionX}px`,previewElement.style.top=`${positionY}px`,zoomImageContainer.style.left=2*-positionX+"px",zoomImageContainer.style.top=2*-positionY+"px"};zoomImage.src=thumb.getAttribute("data-image"),thumb.addEventListener("mousemove",e=>calculatePosition(e.pageX-thumb.offsetLeft,e.pageY-thumb.offsetTop)),thumb.addEventListener("mouseover",_=>previewElement.style.display=""),thumb.addEventListener("mouseout",_=>previewElement.style.display="none");